import { useState } from 'react';

interface IButtonProps {
  color: string
}

const Button = ({ color }: IButtonProps) => {
  const [counter, setCounter] = useState(0)

  const setingCounter = () => {
    setCounter(counter + 1)
  }

  return (
    <>
      <button type="button" style={{backgroundColor: color}} onClick={setingCounter}>Aumentar contagem</button>
      <strong>Contador{counter}</strong>
    </>
  );
}

export default Button;
